import {
  Box,
  Image,
  Text,
  Badge,
  Flex,
  IconButton,
  Skeleton,
} from '@chakra-ui/react';
import { BiExpand } from 'react-icons/bi';

const ProductCard = ({ product, loading }) => {
  return <Skeleton isLoaded={!loading} _hover={{ size: 1.5 }}></Skeleton>;
};

export default ProductCard;
